import React, { Component } from "react";

class Postform extends Component {
  constructor(props) {
    super(props);
    this.state = {
      title: "",
      body: ""
    };
  }

  onChange = e => {};

  render() {
    return (
      <div>
        <h1>Add Post</h1>
        <form>
          <div>
            <label>Title: </label>
            <br />
            <input
              type="text"
              name="title"
              onChange={this.onChange}
              value={this.state.title}
            />
          </div>
          <br />
          <div>
            <label>Body: </label>
            <br />
            <textarea name="body" />
          </div>
          <br />
          <button
            type="submit"
            onChange={this.onChange}
            value={this.state.body}
          >
            Submit
          </button>
        </form>
      </div>
    );
  }
}
export default Postform;
